Rules.InvariantScript:
Switch(
    Upper(ThisItem.Action),
    "DISPLAY",
    Set(
        varDisplay,
        ThisItem.Name
    ),
    "NAVIGATE",
    Navigate(ThisItem.Screen),
    "LAUNCH",
    Launch(ThisItem.LaunchURL),
    "SUBMENU",
    If(
        ThisItem.Name = actionSelected,
        Reset(MenuGallery_Horiz);
        Set(
            actionSelected,
            ""
        ),
        Set(
            actionSelected,
            ThisItem.Name
        );
    );
);

//Select this item
Select(Parent);
//perform some other action?
//
//then navigate to screen
//Navigate(ThisItem.Screen);

If(
    cmp_Menu_Horizontal_MS.ShowHelp,
    Table(
        cmp_Menu_Horizontal.ItemsList,
        {
            Name: "Help",
            Tooltip: "Help Options",
            Icon: Icon.Help,
            ShowIcon: true,
            ShowName: false,
            Action: "Submenu",
            Screen: App.ActiveScreen,
            LaunchURL: "",
            SubmenuList: [
                {
                    Name: "Request",
                    Tooltip: "Click here to return to the Home Screen"
                },
                {
                    Name: "HowTo",
                    Action: "Launch",
                    LaunchURL: howToURL&appReference
                }
            ]
        }
    ),
    cmp_Menu_Horizontal.ItemsList
)

